// Get the parsed AI response
const aiData = $input.item.json;

// Get the original file info from earlier in the workflow
const fileInfo = $('Filter PDFs Only').item.json;

// Format the Google Drive link
const driveLink = `https://drive.google.com/file/d/${fileInfo.id}/view?usp=drivesdk`;

// Get current date in YYYY-MM-DD format
const today = new Date().toISOString().split('T')[0];

// Prepare the data for Google Sheets
return {
  'Date Applied': today,
  'Candidate Name': aiData.name,
  'CV Link': driveLink,
  'Score': aiData.overall_score,
  'Category': aiData.category,
  'Matched Keywords': aiData.matchedSkills.join(', '),
  'Red Flags': aiData.redFlags.length > 0 ? aiData.redFlags.join(', ') : 'None',
  'Status': 'AI Reviewed',
  'AI Recommendation': aiData.recommendation,
  'Years Experience': aiData.yearsExperience,
  'Strengths': aiData.strengths.join('; '),
  '_processingSuccess': true,
  '_fileId': fileInfo.id,
  '_fileName': fileInfo.name
};
