// Store file information for error handling
const fileInfo = {
  id: $input.item.json.id,
  name: $input.item.json.name,
  mimeType: $input.item.json.mimeType
};

// Pass through all data plus file info
return {
  ...$input.item.json,
  fileInfo: fileInfo,
  originalFileId: fileInfo.id
};
