parameters.jsCode:
// Scoring criteria
const scoringCriteria = {
  requiredSkills: ['python', 'javascript', 'n8n', 'automation', 'api', 'google workspace'],
  bonusSkills: ['typescript', 'node.js', 'workflow', 'integration', 'docker'],
  experienceKeywords: ['years experience', 'senior', 'lead', 'managed'],
  educationKeywords: ['bachelor', 'master', 'degree', 'computer science'],
  redFlags: ['gap in employment', 'frequent job changes']
};

// Get CV content
const cvContent = $input.item.binary.data.data.toLowerCase();
const fileName = $input.item.json.name;

// Initialize scoring
let score = 0;
let matchedKeywords = [];
let redFlags = [];
let category = 'unqualified';

// Check required skills (10 points each)
scoringCriteria.requiredSkills.forEach(skill => {
  if (cvContent.includes(skill.toLowerCase())) {
    score += 10;
    matchedKeywords.push(skill);
  }
});

// Check bonus skills (5 points each)
scoringCriteria.bonusSkills.forEach(skill => {
  if (cvContent.includes(skill.toLowerCase())) {
    score += 5;
    matchedKeywords.push(skill);
  }
});

// Check experience (5 points each)
scoringCriteria.experienceKeywords.forEach(keyword => {
  if (cvContent.includes(keyword.toLowerCase())) {
    score += 5;
  }
});

// Check education (5 points)
scoringCriteria.educationKeywords.forEach(keyword => {
  if (cvContent.includes(keyword.toLowerCase())) {
    score += 5;
    break;
  }
});

// Check for red flags
scoringCriteria.redFlags.forEach(flag => {
  if (cvContent.includes(flag.toLowerCase())) {
    redFlags.push(flag);
    score -= 10;
  }
});

// Categorize based on score
if (score >= 50) {
  category = 'qualified';
} else if (score >= 30) {
  category = 'possibly qualified';
} else {
  category = 'unqualified';
}

// Return structured data
return {
  fileName: fileName,
  fileId: $input.item.json.id,
  webViewLink: $input.item.json.webViewLink,
  score: score,
  category: category,
  matchedKeywords: matchedKeywords.join(', '),
  redFlags: redFlags.join(', ') || 'None',
  dateProcessed: new Date().toISOString(),
  status: 'New'
};

